import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { Link, useNavigate } from 'react-router-dom';

interface User {
  id: number;
  nome: string;
  cognome: string;
}

interface Auto {
  id: number;
  modello: string;
  targa: string;
}

const Dashboard: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [auto, setAuto] = useState<Auto[]>([]);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    // Recupera utenti
    axios.get('/user/findAllUsers', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      const list = res.data.userBeanList;
      if (Array.isArray(list)) {
        setUsers(list);
      } else {
        console.error('userBeanList non è un array:', list);
      }
    })
    .catch(err => {
      console.error('Errore nel caricamento utenti:', err);
    });

    // Recupera auto
    axios.get('/automezzi/findAllAuto')
    .then(res => {
      console.log('✅ Risposta Auto:', res.data);
      const list = res.data.autoBeanList;
      if (Array.isArray(list)) {
        setAuto(list);
      } else {
        console.error('❌ autoBeanList non è un array:', list);
      }
    })
    .catch(err => {
      console.error('❌ Errore nel caricamento auto:', err);
    });
  

  }, [token, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Dashboard</h2>
      <button onClick={handleLogout}>Logout</button>

      <h3>Utenti</h3>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            <Link to={`/user/${user.id}`}>{user.nome} {user.cognome}</Link>
          </li>
        ))}
      </ul>

      <h3>Automezzi</h3>
      <ul>
        {auto.map(a => (
          <li key={a.autoId}>
            <Link to={`/auto/${a.autoId}`}>{a.modello} - {a.targa}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
