import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { AcquistiBean } from '../types/AcquistiTypes'; // crea tipo se non l’ha/i
import './AcquistiPage.css';

const AcquistiPage: React.FC = () => {
  const [acquisti, setAcquisti] = useState<AcquistiBean[]>([]);
  const [totaleSpesa, setTotaleSpesa] = useState<number>(0);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return;

    axios.get('/acquisti/findAcquistiByUserId', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(res => {
      if (res.data.errorCode === 0) {
        setAcquisti(res.data.acquistiBeanList || []);
        setTotaleSpesa(res.data.spesaTotale || 0);
      }
    })
    .catch(err => {
      console.error("Errore nel recupero acquisti:", err);
    });
  }, []);

  return (
    <div className="acquisti-container">
      <h2>I miei acquisti</h2>
      <p><strong>Totale speso:</strong> € {totaleSpesa.toFixed(2)}</p>

      <div className="acquisti-lista">
        {acquisti.map((acq, index) => (
          <div key={index} className="acquisto-box">
            {acq.autoBean && (
              <p>Auto: {acq.autoBean.mezzoBean?.modello} - € {acq.autoBean.mezzoBean?.prezzo}</p>
            )}
            {acq.motoBean && (
              <p>Moto: {acq.motoBean.mezzoBean?.modello} - € {acq.motoBean.mezzoBean?.prezzo}</p>
            )}
            {acq.tirBean && (
              <p>Tir: {acq.tirBean.mezzoBean?.modello} - € {acq.tirBean.mezzoBean?.prezzo}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default AcquistiPage;
