// Nuova versione della dashboard migliorata lato UI con 3 bottoni per ogni voce e aggiunta utente
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from '../api/axiosInstance';
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faPen, faTrash, faPlus } from '@fortawesome/free-solid-svg-icons';

interface User {
    userId: number;
    nome: string;
    cognome: string;
}

interface MezzoBean {
    mezzoId: number;
    modello: string;
    targa: string;
    marca: string;
    colore: string;
}

interface Auto {
    autoId: number;
    mezzoBean?: MezzoBean;
}

const Dashboard: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);
    const [auto, setAuto] = useState<Auto[]>([]);
    const [showUsers, setShowUsers] = useState(false);
    const [showAuto, setShowAuto] = useState(false);
    const navigate = useNavigate();

    const getNomeUtenteFromToken = (): string => {
        const token = localStorage.getItem('token');
        if (!token) return '';
        try {
            const payload = token.split('.')[1];
            const decoded = JSON.parse(atob(payload));
            return `${decoded.nome || ''} ${decoded.cognome || ''}`.trim();
        } catch (e) {
            console.error('Errore nel parsing del token:', e);
            return '';
        }
    };

    const nomeUtente = getNomeUtenteFromToken();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        axios.get('/user/findAllUsers')
            .then(res => {
                const list = res.data.userBeanList;
                if (Array.isArray(list)) setUsers(list);
            })
            .catch(err => console.error('Errore nel caricamento utenti:', err));

        axios.get('/automezzi/findAllAuto')
            .then(res => {
                const list = res.data.autoBeanList;
                if (Array.isArray(list)) setAuto(list);
            })
            .catch(err => console.error('Errore nel caricamento auto:', err));
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    const handleAddUser = () => {
        navigate('/user/new');
    };

    const handleDelete = async (userId: number) => {
        if (!window.confirm(`Sei sicuro di voler eliminare l'utente?`)) return;

        try {
            const res = await axios.delete(`/user/deleteUser/${userId}`);
            if (res.data.errorCode === 0) {
                // Aggiorna la lista utenti filtrando quello eliminato
                setUsers(prev => prev.filter(u => u.userId !== userId));
                alert('✅ Utente eliminato con successo');
            } else {
                alert(`⚠️ Errore: ${res.data.errorMessage}`);
            }
        } catch (err) {
            console.error('Errore durante eliminazione utente:', err);
            alert('❌ Errore durante la richiesta al server.');
        }
    };


    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <div className="dashboard-username">Benvenuto{nomeUtente ? `, ${nomeUtente}` : ''}</div>
                <button className="logout" onClick={handleLogout}>Logout</button>
            </div>

            <div className="user-section">
                <div className="user-header">
                    <h3 onClick={() => setShowUsers(!showUsers)}>
                        {showUsers ? '▼' : '▶'} Utenti
                    </h3>
                    {showUsers && (
                        <Link to="/user/new">
                            <button className="create-btn">
                                <FontAwesomeIcon icon={faPlus} /> Aggiungi Utente
                            </button>
                        </Link>
                    )}
                </div>

                {showUsers && (
                    <table className="user-table">
                        <thead>
                            <tr>
                                <th>Nome</th>
                                <th>Azioni</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user.userId}>
                                    <td className="user-name">{user.nome} {user.cognome}</td>
                                    <td className="user-actions">
                                        <Link to={`/user/${user.userId}`}>
                                            <button className="view-btn">
                                                <FontAwesomeIcon icon={faEye} />
                                            </button>
                                        </Link>
                                        <Link to={`/user/edit/${user.userId}`}>
                                            <button className="edit-btn">
                                                <FontAwesomeIcon icon={faPen} />
                                            </button>
                                        </Link>
                                        <button className="delete-btn" onClick={() => handleDelete(user.userId)}>
                                            <FontAwesomeIcon icon={faTrash} />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>


            <div className="toggle-section">
                <h3 onClick={() => setShowAuto(!showAuto)} className="section-title">
                    {showAuto ? '▼' : '▶'} Automezzi
                </h3>
                {showAuto && (
                    <div className="section-content">
                        {auto.map(a => (
                            <div key={a.autoId} className="item-row">
                                <span>{a.mezzoBean?.modello} - {a.mezzoBean?.targa}</span>
                                <div className="action-buttons">
                                    <Link to={`/auto/${a.autoId}`} className="btn-small">
                                        <FontAwesomeIcon icon={faEye} />
                                    </Link>
                                    <button className="btn-small">
                                        <FontAwesomeIcon icon={faPen} />
                                    </button>
                                    <button className="btn-small btn-delete">
                                        <FontAwesomeIcon icon={faTrash} />
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
