import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { Link, useNavigate } from 'react-router-dom';
import '../App.css';


interface User {
  id: number;
  nome: string;
  cognome: string;
}

interface MezzoBean {
    mezzoId: number;
    modello: string;
    targa: string;
    marca: string;
    colore: string;
    // puoi aggiungere altri campi se servono
  }
  
  interface Auto {
    autoId: number;
    mezzoBean?: MezzoBean; // optional in caso non presente
  }
  

const Dashboard: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [auto, setAuto] = useState<Auto[]>([]);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();
  const [showUsers, setShowUsers] = useState(false);
  const [showAuto, setShowAuto] = useState(false);
  
  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    // Recupera utenti
    axios.get('/user/findAllUsers', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      const list = res.data.userBeanList;
      if (Array.isArray(list)) {
        setUsers(list);
      } else {
        console.error('userBeanList non è un array:', list);
      }
    })
    .catch(err => {
      console.error('Errore nel caricamento utenti:', err);
    });

    // Recupera auto
    axios.get('/automezzi/findAllAuto')
    .then(res => {
      console.log('✅ Risposta Auto:', res.data);
      const list = res.data.autoBeanList;
      if (Array.isArray(list)) {
        setAuto(list);
      } else {
        console.error('❌ autoBeanList non è un array:', list);
      }
    })
    .catch(err => {
      console.error('❌ Errore nel caricamento auto:', err);
    });
  

  }, [token, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
    <h2>Dashboard</h2>
    <button onClick={handleLogout}>Logout</button>
  
    <div className="toggle-section">
      <h3 onClick={() => setShowUsers(!showUsers)}>
        {showUsers ? '▼' : '▶'} Utenti
      </h3>
      {showUsers && (
        <ul>
          {users.map(user => (
            <li key={user.id}>
              <Link to={`/user/${user.id}`}>{user.nome} {user.cognome}</Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  
    <div className="toggle-section">
      <h3 onClick={() => setShowAuto(!showAuto)}>
        {showAuto ? '▼' : '▶'} Automezzi
      </h3>
      {showAuto && (
        <ul>
          {auto.map(a => (
            <li key={a.autoId}>
              <Link to={`/auto/${a.autoId}`}>
                {a.mezzoBean?.modello} - {a.mezzoBean?.targa}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  </div>
  
  );
};

export default Dashboard;
