import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUsers, faCar, faClock, faUserPlus, faSearch, faSync } from '@fortawesome/free-solid-svg-icons';
import './DashboardEnhancements.css';

interface DashboardEnhancementsProps {
  userCount: number;
  autoCount: number;
  lastUpdate: string;
  recentItems: { type: 'user' | 'auto'; name: string; extra?: string; date: string }[];
}

const DashboardEnhancements: React.FC<DashboardEnhancementsProps> = ({ userCount, autoCount, lastUpdate, recentItems }) => {
  return (
    <div className="dashboard-enhancements">
      <div className="summary-boxes">
        <div className="summary-box">
          <FontAwesomeIcon icon={faUsers} className="summary-icon" />
          <div className="summary-content">
            <h4>Totale Utenti</h4>
            <p>{userCount}</p>
          </div>
        </div>
        <div className="summary-box">
          <FontAwesomeIcon icon={faCar} className="summary-icon" />
          <div className="summary-content">
            <h4>Totale Automezzi</h4>
            <p>{autoCount}</p>
          </div>
        </div>
        <div className="summary-box">
          <FontAwesomeIcon icon={faClock} className="summary-icon" />
          <div className="summary-content">
            <h4>Ultimo Aggiornamento</h4>
            <p>{lastUpdate}</p>
          </div>
        </div>
      </div>

      <div className="recent-section">
        <h3>Ultimi Inserimenti</h3>
        <ul>
          {recentItems.map((item, index) => (
            <li key={index}>
              {item.type === 'user' ? 'ðŸ‘¤' : 'ðŸš—'} {item.name} {item.extra && `(${item.extra})`} - <span>{item.date}</span>
            </li>
          ))}
        </ul>
      </div>

      <div className="quick-actions">
        <button className="quick-btn"><FontAwesomeIcon icon={faUserPlus} /> Nuovo Utente</button>
        <button className="quick-btn"><FontAwesomeIcon icon={faSearch} /> Cerca</button>
        <button className="quick-btn"><FontAwesomeIcon icon={faSync} /> Ricarica</button>
      </div>
    </div>
  );
};

export default DashboardEnhancements;
