// DashboardEnhancements.tsx
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUsers, faCar, faClock, faPlus, faSync, faSearch } from '@fortawesome/free-solid-svg-icons';
import './DashboardEnhancements.css';

interface DashboardEnhancementsProps {
  userCount: number;
  autoCount: number;
  lastUpdate: string;
  recentItems: { type: 'user' | 'auto'; name: string; extra?: string; date: string }[];
}

const DashboardEnhancements: React.FC<DashboardEnhancementsProps> = ({
  userCount,
  autoCount,
  lastUpdate,
  recentItems
}) => {
  return (
    <div className="dashboard-enhancements">
      <div className="stats-grid">
        <div className="stat-box">
          <FontAwesomeIcon icon={faUsers} className="stat-icon" />
          <h4>Utenti</h4>
          <span>{userCount}</span>
        </div>
        <div className="stat-box">
          <FontAwesomeIcon icon={faCar} className="stat-icon" />
          <h4>Automezzi</h4>
          <span>{autoCount}</span>
        </div>
        <div className="stat-box">
          <FontAwesomeIcon icon={faClock} className="stat-icon" />
          <h4>Ultima Modifica</h4>
          <span>{lastUpdate}</span>
        </div>
      </div>

      <div className="recent-section">
        <h3>Ultimi Inserimenti</h3>
        <ul>
          {recentItems.map((item, idx) => (
            <li key={idx}>
              <span className="recent-type">{item.type === 'user' ? 'ðŸ‘¤' : 'ðŸš—'}</span>
              <strong>{item.name}</strong> {item.extra && `- ${item.extra}`}<br />
              <small>{item.date}</small>
            </li>
          ))}
        </ul>
      </div>

      <div className="quick-actions">
        <h3>Azioni Rapide</h3>
        <button className="quick-btn"><FontAwesomeIcon icon={faPlus} /> Crea Utente</button>
        <button className="quick-btn"><FontAwesomeIcon icon={faPlus} /> Crea Auto</button>
        <button className="quick-btn"><FontAwesomeIcon icon={faSync} /> Ricarica</button>
        <button className="quick-btn"><FontAwesomeIcon icon={faSearch} /> Cerca</button>
      </div>
    </div>
  );
};

export default DashboardEnhancements;
