import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

interface User {
  id: number;
  nome: string;
  cognome: string;
}

interface Auto {
  id: number;
  modello: string;
  targa: string;
}

const Dashboard: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [auto, setAuto] = useState<Auto[]>([]);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) return navigate('/login');

    axios.get('http://localhost:8080/VroomERPRest/rest/user/findAllUsers', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setUsers(res.data.userBeanList));

    axios.get('http://localhost:8080/VroomERPRest/rest/automezzi/findAllAuto', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setAuto(res.data.autoBeanList));
  }, [token, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Dashboard</h2>
      <button onClick={handleLogout}>Logout</button>

      <h3>Utenti</h3>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            <Link to={`/user/${user.id}`}>{user.nome} {user.cognome}</Link>
          </li>
        ))}
      </ul>

      <h3>Automezzi</h3>
      <ul>
        {auto.map(a => (
          <li key={a.id}>
            <Link to={`/auto/${a.id}`}>{a.modello} - {a.targa}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
